<?php
namespace App\Services;

use Carbon\Carbon;
use App\Services\PdfMakerService as XPDF;

class PdfBondelivraisonService
{
    public $pdf;

    function __construct($item){

      XPDF::setHeaderCallback(function($pdf) {
        $pdf->SetFont('helvetica', 'I', 8);
        $pdf->writeHTMLCell(0, 0, '', '', '<br><hr>', 0, 1, 0, true, '', true);
      });
      XPDF::setFooterCallback(function($pdf) {
        $pdf->SetY(-15);
        $pdf->SetFont('helvetica', 'I', 8);
        $pdf->writeHTMLCell(0, 0, '', '', '<hr><div style="text-align:center;">58 Boulevard Anoual Etage 2 N°5 Casablanca, Maroc - Tel: +212 (0) 522 86 22 04 - Fax: +212 (0) 522 86 22 14 - Email: contact@hsprint.ma<br>ICE 001964050000065 - RC 382373 - Taxe Professionnelle 36339335 - IF 24864411</div>', 0, 1, 0, true, '', true);
      });

      XPDF::SetCreator('MEDICOM v1');
      XPDF::SetAuthor('HS-PRINT-SERVICE');
      XPDF::SetTitle('Bon de livraison - HS Print');
      XPDF::SetSubject('Bon de livraison Hs Print Service');
      XPDF::SetKeywords('Bon de livraison, hs, print, service, impression');


      XPDF::SetAutoPageBreak(TRUE, PDF_MARGIN_BOTTOM);

      // set font
      XPDF::SetFont('helvetica', 'B', 20);


      // add a page
      XPDF::AddPage();



      XPDF::SetFont('helvetica', '', 12);
      $tbl = $this->getMyHeader($item);
      XPDF::writeHTML($tbl, true, false, false, false, '');

      XPDF::SetFont('helvetica', '', 22);
      $tbl = '<table><tr><td align="center">Bon de livraison N° '.rand(1000, 9999).'</td></tr></table>';
      XPDF::writeHTML($tbl, true, false, false, false, '');

      XPDF::SetFont('helvetica', '', 11);
      $tbl = $this->getNames($item);
      XPDF::writeHTML($tbl, true, false, false, false, '');

      XPDF::SetFont('helvetica', '', 9);
      $tbl = $this->getProducts($item);
      XPDF::writeHTML($tbl, true, false, false, false, '');

      $tbl = $this->getModalites($item);
      XPDF::writeHTML($tbl, true, false, false, false, '');

      $tbl = $this->getTotals($item);
      XPDF::writeHTML($tbl, true, false, false, false, '');

      $tbl = $this->getCachet();
      XPDF::writeHTML($tbl, true, false, false, false, '');


      $Filename = 'HS_Facture_'.$item->id.'.pdf';
      XPDF::Output($Filename, 'I');

    }

    public function getModalites($item)
    {
      return '<br/>
        <table cellspacing="0" cellpadding="5" border="1">
            <tr>
            <td width="540"><b>Modalités de paiement:</b><br><i><u>Echéanciers:</u>&nbsp;&nbsp; <b>50%</b> à la commande par chèque, <b>50%</b> à la livraison par chèque.</i></td>
            </tr>
        </table>
        ';
    }
    public function getCachet()
    {
      return '<br/>
        <table cellspacing="0" cellpadding="5" border="0">
            <tr>
            <td width="250" align="center"></td>
            <td width="20" align="center"><b></b></td>
            <td width="270" style="border: 1px solid black;"><u><i>Cachet:<br /></i></u><br><br><br><br><br>*Cadre réservé à ne pas remplir avant la récéption.</td>
            </tr>

        </table>
        ';
    }

    public function getTotals($item)
    {
      return '
        <table cellspacing="0" cellpadding="5" border="0">
            <tr>
            <td width="250" style="border: 1px solid black;" rowspan="3"><b>Coordonnées bancaires:</b><br><br><i><u>Banque:</u>&nbsp;&nbsp;&nbsp;BMCE BANK Maroc.</i></td>
            <td width="20" align="center"><b></b></td>
            <td width="140" align="center" style="border: 1px solid black;background-color:#616161;color:#fff;"> <b>Montant total HT (MAD)</b></td>
            <td width="130" align="center" style="border: 1px solid black;"><b><i>'.number_format($item->totalHT,2,'.',',').'</i></b></td>
            </tr>
            <tr>
            <td width="20" align="center"><b></b></td>
            <td width="140" align="center" style="border: 1px solid black;background-color:#616161;color:#fff;" > <b>TVA</b></td>
            <td width="130" align="center" style="border: 1px solid black;"><b>20%</b></td>
            </tr>
            <tr>
            <td width="20" align="center"><b></b></td>
            <td width="140" align="center" style="border: 1px solid black;background-color:#616161;color:#fff;"> <b>Montant total TTC (MAD)</b></td>
            <td width="130" align="center" style="border: 1px solid black;"><b><i>'.number_format($item->totalTTC,2,'.',',').'</i></b></td>
            </tr>
        </table>
        ';
    }

    public function getProducts($item)
    {
      $start = '
          <table border="1" cellpadding="2" cellspacing="0">
          <thead>
           <tr style="background-color:#616161;color:#fff;">
            <td width="20" align="center"><b></b></td>
            <td width="220" align="center"><b>Produit</b></td>
            <td width="80" align="center"><b>Quantité</b></td>
            <td width="100" align="center"> <b>Prix Unité (MAD)</b></td>
            <td width="120" align="center"><b>PrixHT (MAD)</b></td>
           </tr>
          </thead>';

          foreach ($item->facturesproduits as $key => $value) {
              $line = '<tr>
               <td width="20" align="center">'.($key+1).'</td>
               <td width="220">'.$value->produit->name.'</td>
               <td width="80" align="center">'.$value->quantite.'</td>
               <td width="100" align="center">'.number_format($value->prixHT,2,'.',',').'</td>
               <td width="120" align="center">'.number_format($value->totalHT,2,'.',',').'</td>
              </tr>
             ';
             $start = $start.$line;
          }
      $end = '</table>';

      return $start.$end;
    }

    public function getNames($item)
    {
      return '
        <table cellspacing="0" cellpadding="5" border="1">
            <tr>
                <td style="background-color:#efeaea;">Entreprise:<br /><br /><strong>HS Print Service S.A.R.L</strong><br /></td>
                <td style="background-color:#efeaea;">Client:<br /><br /><strong>'.$item->client->name.'</strong><br /></td>
            </tr>
        </table>
        ';
    }


    public function getMyHeader($item)
    {
      return '
      <table cellspacing="0" cellpadding="1">
          <tr>
              <td style="text-align:right;"><img width="100px" src="data:image/png;base64," alt="HS Print Service">
              <br /></td>
              <td><br><img src="data:image/png;base64," width="45"/>
              <br><strong>BL #ID: <br></strong>'.uniqid().'-'.$item->id.'<br><strong>Date de génération:</strong><br>'.$item->created_at.'</td>
          </tr>
      </table>
      ';
    }

}
